import Axios from 'axios';
import { useState } from 'react';
import './websitepassform.css';
import 'firebase/auth';
import firebase from "../../firebase"


function PassForm() {


  const [password,setpassword] = useState('');
  const [title,settitle] = useState('');

 const addPassword = () => {
  Axios.post('http://localhost:3001/addpassword', {password: password, title: title});

  

 }
 // ab dekh
 // current user dikh rha h ab
// acha
//null dikh rha hai
 function showCurrentUser(){
   
  firebase.auth().onAuthStateChanged(function(user) {
    if (user) {
      console.log(user);
    } else {
      // No user is signed in.
    }
  });
 }


 
 function onlogout(){
  firebase.auth().signOut().then(() => {
    // Sign-out successful.
  }).catch((error) => {
    // An error happened.
  });
  // window.location.replace('/');
}


 return <div className="Appy">
    <div className="AddingPassword">
      <input type="text" placeholder="Example pass123" onChange={(event) => { setpassword(event.target.value)}}/>
      <input type="text" placeholder="Example www.facebook.com" onChange={(event) => { settitle(event.target.value)}} />
      <button onClick={addPassword}>Add Password </button>
      <button onClick={onlogout}>Signout </button>
      <button onClick={showCurrentUser}>Who are you!</button>
    </div>

  </div>;
}

export default PassForm;
