import react, { useState } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import 'firebase/auth';
// const db=firebase.firestore().collection('manager');
import firebase from '../../firebase';
import Mainpage from '../MainPage/mainpage';
// https://github.com/shantanu16101998/chat_app/blob/main/client/src/components/Chat/Chat.js





export default function ShowPass() {



    const [passList,setPasslist] = useState([])
    var count = 0;
    function onsubmit(){
       try{
           
           const manager_ref=firebase.firestore().collection('manager');
            console.log(firebase.auth().currentUser.email);
            const passwordref = manager_ref.where("user","==",firebase.auth().currentUser.email);

            
            //ab loading bhi fast ho rahi ab bs css sahi krna hai
            // abhi password decrypt krna pdega
            // ha yr bas ho jaega
            // http://localhost:3000/passwords
            // isme ja tere pass dikh jaenge
            passwordref.get().then((querySnapshot) =>{
                querySnapshot.forEach((doc) => {
                    var passInfo = {
                        website: doc.data().website,
                        password: doc.data().password,
                    }
                    var pass = doc.data().password;
                    // console.log(pass)
                    setPasslist(passList => [ ...passList, passInfo ]);
                })
            })
        }
        catch (e){
            console.log(e);
        }
    }


    return (
          <div className="" onLoad={onsubmit}>
              <button onClick={onsubmit}>Show</button>
            <h1 className="">These are my passwords</h1>
            <table>
                <tbody>
            {passList.map((val,i)=>{
                    return <tr className="p" key={i}>
                            <td>{val.website}</td>
                            <td>{val.password}</td>                        
                            </tr>
                })} 
                </tbody>
            </table>
                 
  </div>

      );
}
