import Axios from 'axios';
import { useState, useEffect } from 'react';
import './websitepassform.css';
import 'firebase/auth';
import firebase from "../../firebase"
const {encrypt,decrypt} = require('./Encryption');
const manager_ref=firebase.firestore().collection('manager');
function PassForm() {


  const [password,setpassword] = useState('');
  const [title,settitle] = useState('');
  const [passwordList,setPasswordList] = useState([])
  const [passList,setPasslist] = useState([]);
  
  useEffect(()=>{
    Axios.get('http://localhost:3001/showpasswords').then((response)=>{
    setPasswordList(response.data)
    })
  },[]);

 const addPassword = () => {
   if(title=="" || password==""){
     alert("Any Field can't be empty");
   }
   else{
  const hashedpassword = encrypt(password);
  manager_ref.add({
    password:hashedpassword.password,
    user:firebase.auth().currentUser.email,
    website:title,
    iv:hashedpassword.iv,
  });
  alert("password added successfully");
  }
  // Axios.post('http://localhost:3001/addpassword', {password: password, title: title, email:firebase.auth().currentUser.email});
  
 };

 

 function showCurrentUser(){
   try{
    console.log(firebase.auth().currentUser.email);
   }
   catch (e){
     console.log(e);
   }
  
 }


 
 function onlogout(){
  firebase.auth().signOut().then(() => {
    window.location.replace('/');
  }).catch((error) => {
    // An error happened.
  });
  // window.location.replace('/');
}

function showMyPass(){
  try{
 
      const manager_ref=firebase.firestore().collection('manager');
       console.log(firebase.auth().currentUser.email);
       const passwordref = manager_ref.where("user","==",firebase.auth().currentUser.email);


       passwordref.get().then((querySnapshot) =>{
           querySnapshot.forEach((doc) => {
               
               var pass = doc.data().password;

               var decryption_material = {
                  password: doc.data().password.toString(),
                  iv: doc.data().iv,
               }


               var passInfo = {
                  website: doc.data().website,
                  password: decrypt(decryption_material),
              }

               setPasslist(passList => [ ...passList, passInfo ]);
           })
       })
       
   }
   catch (e){
       console.log(e);
   }

}
// const [passworrd,setP]=useState('');
const [searchTitle,setSearchTitle]=useState('');
const [searchObjectitle,setObject]=useState('');
function search(){
  if(searchTitle==""){
    alert("Website Field cant be empty");
  }
  else{
  try{
    const manager=firebase.firestore().collection('manager');
    console.log(firebase.auth().currentUser.email);
    const passwordr = manager.where("user","==",firebase.auth().currentUser.email).where("website","==",searchTitle);
    console.log(passwordr);
    passwordr.get().then((querySnapshot) =>{
      querySnapshot.forEach((doc) => {
   
      setObject(doc.data());

      });
    });
    var decypt;
    if(searchObjectitle!=null){
    decypt={
      password: searchObjectitle.password.toString(),
      iv: searchObjectitle.iv, 
    }
    // setP(decrypt(decypt));
   
  }
  else{
    alert("website not found in your record");
  }
    alert(decrypt(decypt)+"search successfull");
  }
  catch(e){
    console.log(e);
  }
}
}


 return <div className="Appy">
    <div className="login-box AddingPassword">
    
      <div class="col-lg-12 login-title">
        ENTER YOUR PASSWORD
                </div>
      <br></br>


      <form>  


        <div class="form-group">
        <label class="form-control-label">PASSWORD</label>
        <br></br>
        <input type="text" placeholder="Example pass123" onChange={(event) => { setpassword(event.target.value)}}/>
          </div>    
        


        <div class="form-group">
        <label class="form-control-label">WEBSITE</label>
        <br></br> 
      <input type="text" placeholder="Example www.facebook.com" onChange={(event) => { settitle(event.target.value)}} />
      </div>
      <br></br>


      <button onClick={addPassword}>Add Password </button>

      
      <br></br>
      <button onClick={onlogout}>Signout </button>
      <br></br>
      {/* <button onClick={showCurrentUser}>Who are you!</button> */}
      <br></br>
      <div className="user-box">
      <label class="form-control-label">SEARCH</label>
      <input type="text" placeholder="Website to Search for Password" onChange={(event) => { setSearchTitle(event.target.value)}} />
      </div>
      <button onClick={search}>Search </button>
      <p>Your Password is : {searchObjectitle.password}</p>
      <button onClick={showMyPass}>Show my pass!</button>
      </form>
            </div>

            



















            
            <table>
                <tbody>
            {passList.map((val,i)=>{
                    return <tr className="p" key={i}>
                            <td>{val.website}</td>
                            <td>{val.password}</td>                        
                            </tr>
                })} 
                </tbody>
            </table>
        </div>
}

export default PassForm;
