import react, { useState } from 'react';
import { BrowserRouter as Router, Route } from "react-router-dom";
import 'firebase/auth';
// const db=firebase.firestore().collection('manager');
import firebase from '../../firebase';
// https://github.com/shantanu16101998/chat_app/blob/main/client/src/components/Chat/Chat.js





export default function ShowPass() {



    const [passList,setPasslist] = useState([])

    function onsubmit(){
       try{
           // ek min rupesh call kr rha h
           //ok
           // bc hmesha paise maangta rehta h ye
           //
            const messageRef=firebase.firestore().collection('manager');
            console.log(firebase.auth().currentUser.email);
            const passwordref = messageRef.orderBy("time").where("user","==","anonymous");
            passwordref.get().then((querySnapshot) =>{
                querySnapshot.forEach((doc) => {
                    var passInfo = {
                        text: doc.data().website,
                    }
                    var pass = doc.data().password
                    setPasslist(pass => [ ...passList, pass ]);
                })
            })
        }
        catch (e){
            console.log(e);
        }
       
    }



    return (
          <div className="joinOuterContainer" onLoad={onsubmit}>
              <button onClick={onsubmit}>Show</button>
            <h1 className="heading">These are my passwords</h1>
            <div className="Passwords">
                {passList.map((val)=>{
                    return <div className="password">
                            <h3>{val.title}</h3>
                            </div>
                })}  
  </div>
          </div>
      );
}
