import Axios from 'axios';
import { useState, useEffect } from 'react';
import './websitepassform.css';
import 'firebase/auth';
import firebase from "../../firebase"
const {encrypt,decrypt} = require('./Encryption');
const manager_ref=firebase.firestore().collection('manager');
function PassForm() {


  const [password,setpassword] = useState('');
  const [title,settitle] = useState('');
  const [passwordList,setPasswordList] = useState([])
  const [passList,setPasslist] = useState([]);
  
  useEffect(()=>{
    Axios.get('http://localhost:3001/showpasswords').then((response)=>{
    setPasswordList(response.data)
    })
  },[]);

 const addPassword = () => {
   if(title=="" || password==""){
     alert("Any Field can't be empty");
   }
   else{
  const hashedpassword = encrypt(password);
  manager_ref.add({
    password:hashedpassword.password,
    user:firebase.auth().currentUser.email,
    website:title,
    iv:hashedpassword.iv,
  });
  alert("password added successfully");
  }
  // Axios.post('http://localhost:3001/addpassword', {password: password, title: title, email:firebase.auth().currentUser.email});
  
 };

 


 
 function onlogout(){
  firebase.auth().signOut().then(() => {
    window.location.replace('/');
  }).catch((error) => {
    // An error happened.
  });
  // window.location.replace('/');
}



function showMyPass(){
  try{
 
      const manager_ref=firebase.firestore().collection('manager');
       console.log(firebase.auth().currentUser.email);
       const passwordref = manager_ref.where("user","==",firebase.auth().currentUser.email);


       passwordref.get().then((querySnapshot) =>{
           querySnapshot.forEach((doc) => {

               var decryption_material = {
                  password: doc.data().password.toString(),
                  iv: doc.data().iv,
               }


               var passInfo = {
                  website: doc.data().website,
                  password: decrypt(decryption_material),
              }
              console.log(passInfo)
               setPasslist(passList => [ ...passList, passInfo ]);
           })
       })
       
   }
   catch (e){
       console.log(e);
   }

}

const [passworrd,setP]=useState('');
const [searchTitle,setSearchTitle]=useState('');
const [searchObjectitle,setObject]=useState('');


function search(searchTitles){
  if(searchTitles==""){
    alert("Website Field cant be empty");
  }
  else{
  try{
    var obj;
    const manager=firebase.firestore().collection('manager');
    console.log(firebase.auth().currentUser.email);
    const passwordr = manager.where("user","==",firebase.auth().currentUser.email).where("website","==",searchTitles);
    console.log(passwordr);
    passwordr.get().then((querySnapshot) =>{
      querySnapshot.forEach((doc) => {
   
      setObject(doc.data());
      obj=doc.data();
      });
    });
    var decypt;
    if(obj!=null){
    decypt={
      password: obj.password.toString(),
      iv: obj.iv, 
    }
    console.log(decypt);
    var s=decrypt(decypt);
    setP(s);
    console.log(s);

   
  }
  else{
    alert("website not found in your record");
  }
    // alert(decrypt(decypt)+"search successfull");
  }
  catch(e){
    console.log(e);
  }
}
}

const [updatedPassword,setU]=useState('');

function update(searchTitles,updates){
  
  del(searchTitles);
  
  
}

function del(searchTitles){
  try{
  const manager=firebase.firestore().collection('manager');
  // manager.
  const passwordr = manager.where("user","==",firebase.auth().currentUser.email).where("website","==",searchTitles);

  passwordr.get().then((querySnapshot) =>{
      querySnapshot.forEach((doc) => {
   
      doc.ref.delete();

      });
    });
    alert
  }
  catch(e){
    console.log(e);
  }
}

return <div className="Appy">
    <div className="login-box AddingPassword">
    
      <div className="col-lg-12 login-title">
        ENTER YOUR PASSWORD
                </div>
      <br></br>


      {/* <form>   */}


        <div className="form-group">
        <label className="form-control-label">PASSWORD</label>
        <br></br>
        <input type="text" placeholder="Example pass123" onChange={(event) => { setpassword(event.target.value);setU(event.target.value);}}/>
          </div>    
        


        <div className="form-group">
        <label className="form-control-label">WEBSITE</label>
        <br></br> 
      <input type="text" placeholder="Example www.facebook.com" onChange={(event) => { settitle(event.target.value);setSearchTitle(event.target.value);}} />
      </div>
      <br></br>


      <button onClick={addPassword}>Add Password </button>
      
      <button onClick={del}>Delete Entry </button>
     
      
      
      <button onClick={onlogout}>Signout </button>
      <br></br>
      {/* <button onClick={showCurrentUser}>Who are you!</button> */}
      <br></br>
      <div className="user-box">
      <label className="form-control-label">Enter the website</label>
      <br></br>
      <input type="text" placeholder="Website to Search for Password" onChange={(event) => { setSearchTitle(event.target.value)}} />
      </div>
      <button onClick={search}>Search </button>
      <button onClick={del}>Delete Password </button>

      <div className="col-lg-12 login-title">
      Your Password is : {passworrd}
                </div>

      <button onClick={showMyPass}>Show all passwords</button>

      {/* </form> */}
            </div>
            <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>

            <table class="table table-striped">
            <thead class="thead-dark">
    <tr>

      <th scope="col">Website</th>
      <th scope="col">Password</th>
    </tr>
  </thead>
  <tbody>
              {passList.map((val,i)=>{

                      return <tr>
                        <th scope="row"><p>{val.website}</p></th>
                        <td><p>{val.password}</p></td>
                          </tr>
                  
                })} 
                </tbody>
              </table>


            <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm" crossorigin="anonymous"></link>
            <br></br>
            <br></br>
            <br></br>
            <br></br>

            <br></br>
            <br></br>
            <br></br>

            <br></br>
            <br></br>
            <br></br>
            <br></br>
            
        </div>
}

export default PassForm;
